        private void cargaDeRandomArt() {
            // Creamos un hilo de ejecución para manejar la solicitud de la API
            ExecutorService executorService = Executors.newSingleThreadExecutor();
            executorService.submit(() -> {
                try {
                    // Creamos una instancia de RandomArtService
                    RandomArtService randomArtService = new RandomArtService();

                    // Obtenemos una lista de objetos JSON
                    List<Artwork> artworks = randomArtService.obtenerArtworksPorIdsAleatorios();

                    // Cuando la tarea esté completa, mostramos un mensaje en la interfaz principal
                    new Handler(Looper.getMainLooper()).post(() -> {
                        // Aquí podemos manejar la UI después de completar la tarea
                        Toast.makeText(MainActivity.this, "Se obtuvieron " + artworks.size() + " objetos.", Toast.LENGTH_SHORT).show();

                        // Imprimir cada objeto en el log
                        for (Artwork artwork : artworks) {
                            Log.d(TAG, "Objeto obtenido: " + artwork.toString());
                        }
                    });
                } catch (IOException e) {
                    e.printStackTrace();
                    new Handler(Looper.getMainLooper()).post(() -> {
                        Toast.makeText(MainActivity.this, "Error al obtener los objetos", Toast.LENGTH_SHORT).show();
                    });
                } catch (InterruptedException e) {
                    throw new RuntimeException(e);
                }
            });
        }

