        private List<Artwork> filterArtworks(String result) {
            List<Artwork> filteredArtworks = new ArrayList<>();
            try {
                JSONObject jsonResult = new JSONObject(result);

                // Verificar si los campos de imagen no están vacíos y si el artista es Pablo Picasso
                if (!jsonResult.getString("primaryImage").isEmpty() &&
                        !jsonResult.getString("primaryImageSmall").isEmpty()) {

                    Artwork artwork = new Artwork(
                            jsonResult.getInt("objectID"),
                            jsonResult.getString("primaryImage"),
                            jsonResult.getString("primaryImageSmall"),
                            jsonResult.getString("objectName"),
                            jsonResult.getString("title"),
                            jsonResult.getString("culture"),
                            jsonResult.getString("period"),
                            jsonResult.getString("artistDisplayName"),
                            jsonResult.getString("artistDisplayBio")
                    );
                    filteredArtworks.add(artwork);
                }
            } catch (JSONException e) {
                e.printStackTrace();
            }
            return filteredArtworks;
        }

        private void saveArtworksToDatabase(List<Artwork> artworks, String collectionTitle) {
            ArtDatabase database = ArtDatabase.getInstance(this);
            ArtworkDao artworkDao = database.artworkDao();
            CollectionDao collectionDao = database.collectionDao();
            ArtworkCollectionCrossRefDao crossRefDao = database.crossRefDao();

            new Thread(() -> {
                Collection impressionismCollection = collectionDao.getCollectionByName(collectionTitle);
                if (impressionismCollection != null) {
                    for (Artwork artwork : artworks) {
                        artworkDao.insert(artwork);
                        ArtworkCollectionCrossRef crossRef = new ArtworkCollectionCrossRef(artwork.getObjectID(), impressionismCollection.getId());
                        crossRefDao.insert(crossRef);
                    }
                    Log.d(TAG, "Artworks saved to the database and associated with the " + collectionTitle + " collection.");
                } else {
                    Log.e(TAG, "Impressionism collection not found.");
                }
            }).start();
        }

